import React, {Component} from 'react';
import {Row, Col, Table, Badge} from 'react-bootstrap';
// import Table from "../../Tables/BootstrapTable";
import Aux from "../../../hoc/_Aux";
import Card from "../../../App/components/MainCard";

class RequestPool extends Component {
    state = {
        poolTable: [
            {count: "1", id: "REQ0001", Sender: "Cynthia Ezechukwu", Amount: 1500, Date: "27/07/19", Vendor: "Daniel Abayomi"},
            {count: "2", id: "REQ0002", Sender: "Daniel Abayomi", Amount: 1500, Date: "27/07/19", Vendor: "Cynthia Ezechukwu"},
            {count: "3", id: "REQ0003", Sender: "Daniel Abayomi", Amount: 1500, Date: "27/07/19", Vendor: "Cynthia Ezechukwu"}
        ]
    }
    render() {
        const poolTable = (
            <span>
            {this.state.poolTable.map(pool => {
                return  <span>
                    <th scope="row">{pool.count}</th>
                    <td>{pool.id}</td>
                    <td>{pool.Sender}</td>
                    <td>&#8358;{pool.Amount}</td>
                    <td>{pool.Date}</td>
                    <td>{pool.Vendor}</td>
                    <td><Badge variant="secondary">Running</Badge></td>
                    <td><Badge variant="warning" size="sm">View</Badge></td>

                </span>
            })}
            </span>
        )
        return (
            <Aux>
                <Row>
                    <Col>
                        <Card title='Request Pool' anotherOption>
                            <Table striped responsive>
                            <thead>
                            <tr>
                                <th>#</th>
                                <th>ReqID</th>
                                <th>Sender</th>
                                <th>Amount</th>
                                <th>Date</th>
                                <th>Vendor</th>
                                <th>Status</th>
                                <th>View</th>
                            </tr>
                            </thead>
                            <tbody>
                            <tr>
                                {poolTable}
                            </tr>
                            </tbody>
                            </Table>
                        </Card>
                    </Col>
                </Row>
            </Aux>
        );
    }
}

export default RequestPool;
